sequence :: [Node] -> Node
sequence []     = Skip
sequence [x]    = x
sequence xs     = foldr Sequence (last xs) (L.init xs)

sequenceExpr :: [Node] -> Node
sequenceExpr [] = Skip
sequenceExpr [x] = Expr x
sequenceExpr xs  = foldr (\l r -> Sequence (Expr l) r) (Expr $ last xs) (L.init xs)  

buildDocument ::  [Node] -> Node -> [Node] -> Node -> [Node] -> Node
