                    b = removed_Syn_Node . execute M.empty M.empty undefined $ a
                    c = fixPoint (extractFunctions_Syn_Node . execute M.empty M.empty undefined) $ b
                    d = extractParameters_Syn_Node . execute M.empty M.empty undefined $ c
                in d


